//使用nodejs的mysql模块实现对MySQL数据库的操作
//导入模块
const mysql = require('mysql');
//创建一个连接对象
const connection = mysql.createConnection({
  host     : '127.0.0.1',
  user     : 'root',
  password : '',
  port: '3306',
  database : 'mydb'
});
//执行连接
connection.connect();

//发送SQL语句并执行结果处理
connection.query('select * from stu',(error, results, fields) => {
  if(error) throw error;
  console.log(results);
});

//执行添加（此处sql使用？（参数式sql）是为了防止sql注入）
let sql = "insert into stu(name,sex,age,classid) values(?,?,?,?)";
let data = ['赵丽',0,21,'php220'];
connection.query(sql,data,(err, result)=>{
    if(err){
         console.log('[INSERT ERROR] - ',err.message);
        return;
    }
    //console.log('INSERT ID:',result);          
    console.log('自增id号:',result.insertId);
    console.log('影响的行数:',result.affectedRows);        
 });

connection.end();
